mapper.select:
select
,SportTypeName,PhotoStyleName
from photophotography ,basicsporttype bst,basicphotostyle bps where 1=1 and bst.SportTypeID = photophotography.SportTypeID and bps.PhotoStyleID = photophotography.StyleTypeID and Title LIKE concat('%',#{photophotoGraphy.title},'%')
and Author LIKE concat('%',#{photophotoGraphy.author},'%')
and PublishTime between #{photophotoGraphy.begintime} and #{photophotoGraphy.endtime}
and photophotography.SportTypeID=#{photophotoGraphy.sporttypeid}
and photophotography.StyleTypeID=#{photophotoGraphy.styletypeid} and CurrentLikeTimes >= CONVERT(#{photophotoGraphy.minliketimes},SIGNED) and CurrentLikeTimes <= CONVERT(#{photophotoGraphy.maxliketimes},SIGNED) and TotalLikeTimes >= CONVERT(#{photophotoGraphy.tminliketimes},SIGNED) and TotalLikeTimes <= CONVERT(#{photophotoGraphy.tmaxliketimes},SIGNED) limit #{page.startIndex},#{page.pageSize}
